	(6079, 'facebook198744036811672_1069915166361217', 'Zlatan Ibrahimovic', 'https://graph.facebook.com/198744036811672/picture?type=normal', 'facebook', 'https://www.facebook.com/198744036811672/posts/1069915166361217', 'https://graph.facebook.com/1069915166361217/picture?type=normal', 'Today is #WorldFoodDay, please support my friends World Food Programme for a world free from hunger by 2030. One Future, #ZeroHunger.', '', 'en', 'Today is #WorldFoodDay, please support my friends World Food Programme for a world free from hunger by 2030. One Future, #ZeroHunger.', '2015-10-16 10:04:55', 375),
